mapper.select:
select
id, username, accountType, lockStatus, valid, personCode, personName, delFlag, createTime, modifyTime, syncTime
from sys_user


        and id = #{sysUser.id}


        and username = #{sysUser.username}


        and accountType = #{sysUser.accounttype}


        and lockStatus = #{sysUser.lockstatus}


        and valid = #{sysUser.valid}


        and personCode = #{sysUser.personcode}


        and personName = #{sysUser.personname}


        and delFlag = #{sysUser.delflag}


        and createTime = #{sysUser.createtime}


        and modifyTime = #{sysUser.modifytime}


        and syncTime = #{sysUser.synctime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_user


        and id = #{id}


        and username = #{username}


        and accountType = #{accounttype}


        and lockStatus = #{lockstatus}


        and valid = #{valid}


        and personCode = #{personcode}


        and personName = #{personname}


        and delFlag = #{delflag}


        and createTime = #{createtime}


        and modifyTime = #{modifytime}


        and syncTime = #{synctime}
